			com1:{
				data(){
				//注意子组件的data数据，并不是通过父组件传递过来的，而是子组件私有的，比如Ajax请求回来的数据，都可以放在data身上
				// data 上的数据都是可读可写的
					return{
						title:'123',
						content:'小名'
					}
				},
				// 注意： 组件种所有props数据都是通过父组件传递给子组件的
				template:'<h1>这是子组件{{parentmsg}}</h1>',
				// props中的数据都是只读的
				props:['parentmsg'] 
				//把父组件传递过来的parentmsg属性，在props定义才能使用这个数据
			}
		},
		methods:{
			// onChange:function(){
			// 	this.parentmsg = "被修改了！"
